import "../style.css";
import "./Icon_data";
import icon_data from "./Icon_data";
import React, { useRef } from "react";
// import "./Slider.css"; // Create and style the slider container in a CSS file



const Nav2 = () => {

        const scrollRef = useRef(null);
      
        const scrollLeft = () => {
          scrollRef.current.scrollBy({
            top: 0,
            left: -200,
            behavior: "smooth"
          });
        };
      
        const scrollRight = () => {
          scrollRef.current.scrollBy({
            top: 0,
            left: 200,
            behavior: "smooth"
          });
        };
      

  return (
    <div>
  <button onClick={scrollLeft} className="arrow left-arrow">
        &lt;
      </button>

      <div class="scrolling-wrapper">
        <div className="icon">
          {icon_data.map((e) => (
            <div className="aa" key={e.id}>
              <img src={e.image} alt={e.title} />
              <>{e.title}</>
            </div>
          ))}
        </div>
      </div>
      <button onClick={scrollRight} className="arrow right-arrow">
        &gt;
      </button>

    </div>
  );
};

export default Nav2;
