import React, { useState } from "react";
import "../style.css";
import { Link } from "react-router-dom";
import languageCountryPairs from "./languageCountryPairs";
import currencies from "./Currancy";

function Header() {
  const [dropdownVisible, setDropdownVisible] = useState(false);
  const [modelOpen, setModelOpen] = React.useState(false);
  const [language, setLanguage] = useState(false);
  const [isRight, setIsRight] = useState(true);

  const [searchView, setSearchView] = useState(false);

  const handleClick = () => {
    setIsRight((prevState) => !prevState);
  };
  const toggleDropdown = () => {
    setDropdownVisible(!dropdownVisible);
  };

  return (
  

   <div style={{display:"flex",justifyContent:"center",paddingTop:"20px",paddingBottom:"20px"}}>
    
      <div style={{display: "flex",width:"60%", padding: "1px", backgroundColor: "#f0f0f0", borderRadius: "210px",justifyContent:"center"}}>
        <div style={{display: "flex", gap: "50px", alignItems: "center", justifyContent: "center", height: "80px"}}>
          <div>
            <div style={{fontWeight: "400"}}>Where</div>
            <div style={{opacity: "0.6"}}>Search Destination</div>
          </div>
          <div>
            <div  style={{fontWeight: "400"}}>Check In</div>
            <div style={{opacity: "0.6"}}>Add Date</div>
          </div>
          <div>
            <div  style={{fontWeight: "400"}}>Check Out</div>
            <div style={{opacity: "0.6"}}>Add Date</div>
          </div>
          <div>
            <div style={{fontWeight: "400"}}>Who</div>
            <div style={{opacity: "0.6"}}>Add Guest</div>
          </div>
          <div onClick={()=> setSearchView(true)} style={{display: "flex", backgroundColor: "red", borderRadius: "30px", width: "120px", alignItems: "center", justifyContent: "center",height:"40px"}}>
            <div>
              <img style={{height: "26px"}} src="./img/ss.png" alt="" />
            </div>
            <div style={{marginLeft: "5px"}}>Search</div>
          </div>
        </div>
      </div>
      </div>
  );
}

export default Header;
