import React, { useRef, useState } from "react";
import { links } from "../Assest/images-links";
import "../style.css";

const Nav2 = ({ selectedFilter, setSelectedFilter}) =>{

  const scrollRef = useRef(null);
   const scrollLeft = () => {
     if (scrollRef.current) {
       scrollRef.current.scrollBy({
         top: 0,
         left: -200,
         behavior: "smooth",
       });
     }
   }
   const scrollRight = () => {
     if (scrollRef.current) {
       scrollRef.current.scrollBy({
         top: 0,
         left: 200,
         behavior: "smooth",
       });
     }
   }

  return (

    <div style={{display:"flex",alignItems:"center"}}>
     <button onClick={scrollLeft} className="arrow-left-arrow">
       &lt;
     </button>


    <div className="scrolling-wrapper">
      {links.map((item, i) => (
        <div
          key={i}
          className="icon"
          onClick={() => {
            console.log("selecting key", i);
            setSelectedFilter(i);
          }}
        >
          <img src={item.imgSrc} className="links-img" />
          <p className="icon img">{item.label}</p>
        </div>
      ))}
    </div>

    <button onClick={scrollRight} className="arrow-right-arrow">
      &gt;
    </button>
  </div>
  );
}

export default Nav2;
