import React, { useRef } from "react";
import "../style.css";
import icon_data from "./Icon_data";

const Nav2 = () => {
  const scrollRef = useRef(null);

  const scrollLeft = () => {
    if (scrollRef.current) {
      scrollRef.current.scrollBy({
        top: 0,
        left: -200,
        behavior: "smooth",
      });
    }
  };

  const scrollRight = () => {
    if (scrollRef.current) {    
      scrollRef.current.scrollBy({
        top: 0,
        left: 200,
        behavior: "smooth",
      });
    }
  };

  return (
    <div  style={{position:"sticky",top:"100px",zIndex:"1000",backgroundColor:"#fff"}}>   
       <div style={{display:"flex",alignItems:"center"}}>
      <button onClick={scrollLeft} className="arrow-left-arrow">
        &lt;
      </button>

      <div className="scrolling-wrapper" ref={scrollRef}>
        {icon_data.map((e) => (
          <div className="icon" key={e.id}>
            <img src={e.image} alt={e.title} />
            <>{e.title}</>
          </div>
        ))}
      </div>

      <button onClick={scrollRight} className="arrow-right-arrow">
        &gt;
      </button>
    </div>
    </div>
  );
};

export default Nav2;
